mapper.select:
select pr.id as id,pr.name,pr.price,pr.stock,pr.desc,pr.shop_name as shopName,pi.url
from(
select pro.id,pro.name,pro.price,pro.stock,pro.desc,MIN(pi.id) as img_id,shop_name
from
(select p.id, p.name, p.price, p.stock, p.like_cnt, p.comment_cnt, p.sale_cnt, s.name as shop_name,p.desc
from product p inner join shop s
on p.shop_id=s.id inner join product_type pt
on p.type_id=pt.id


        where pt.id=#{typeId,jdbcType=INTEGER} and s.id=#{shopId,jdbcType=INTEGER}




                where s.id=#{shopId,jdbcType=INTEGER}




                        where pt.id=#{typeId,jdbcType=INTEGER}






) as pro inner join product_image pi
on pro.id = pi.product_id
group by pro.id,pro.name,pro.price,pro.stock,pro.desc,pro.shop_name
)as pr INNER JOIN product_image pi
on pr.img_id=pi.id
limit #{startIndex},#{pageSize}
